package com.example.taskmanager.ui.onboarding

import android.view.LayoutInflater
import android.view.ViewGroup
import androidx.core.view.isVisible
import androidx.recyclerview.widget.RecyclerView.Adapter
import androidx.recyclerview.widget.RecyclerView.ViewHolder
import com.example.taskmanager.databinding.ItemOnboardingBinding
import com.example.taskmanager.model.OnBoarding
import com.example.taskmanager.ui.utils.loadImage

class OnBoardingAdapter(private val onClick: () -> Unit) :
    Adapter<OnBoardingAdapter.OnboardingViewHolder>() {
    private val list = arrayListOf(
        OnBoarding(
            "https://avatars.mds.yandex.net/i?id=aa55c1996061b76702c024f5d96a4162ba422a7f-9148772-images-thumbs&n=13",
            "Title 1",
            "Desc 1"
        ),
        OnBoarding(
            "https://w7.pngwing.com/pngs/731/105/png-transparent-computer-icons-task-management-virtual-assistant-symbol-symbol-miscellaneous-text-service.png",
            "Title 2",
            "Desc 2"
        ),
        OnBoarding(
            "data:image/jpeg;base64,",
            "Title 3",
            "Desc 3"
        )
    )

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): OnboardingViewHolder {
        return OnboardingViewHolder(
            ItemOnboardingBinding.inflate(
                LayoutInflater.from(parent.context),
                parent,
                false
            )
        )
    }

    override fun getItemCount(): Int {
        return list.size
    }

    override fun onBindViewHolder(holder: OnboardingViewHolder, position: Int) {
        holder.bind(list.get(position))
    }

    inner class OnboardingViewHolder(private val binding: ItemOnboardingBinding) :
        ViewHolder(binding.root) {
        fun bind(onBoarding: OnBoarding) = with(binding) {
            tvTitle.text = onBoarding.title
            tvDesc.text = onBoarding.desc
            ivBoard.loadImage(onBoarding.image)
            skip.setOnClickListener {
                onClick()
            }
            btnStart.setOnClickListener {
                onClick()
            }
            skip.isVisible = adapterPosition != list.lastIndex
            btnStart.isVisible = adapterPosition == list.lastIndex
        }
    }
}
